class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){if(1==t.deleted)if("Post"==t.type){let l=$(".like-comment-box");for(let e of l){let l=$(e).attr("id");if(t.likeable==l){let t=parseInt($(e).text());1==t?$(e).html(""):(t-=1,$(e).html(`<p style="margin: 13px 8px">\n                                            <i class="fa-solid fa-thumbs-up color"></i> <span class="post-like">${t}</span>\n                                        </p>`))}}$(e).css("color","gray")}else{let l=$(".comment-like");for(let e of l){if($(e).attr("id")==t.likeable){let t=parseInt($(e).text());1==t?$(e).html(""):(t-=1,$(e).html(`<span>\n                                                    <i class="fa-solid fa-thumbs-up color"></i> ${t} \n                                            </span>`))}}$(e).css("color","gray")}else if("Post"==t.type){let l=$(".like-comment-box");for(let e of l){let l=$(e).attr("id");if(t.likeable==l){let t=parseInt($(e).text());isNaN(t)?$(e).html('<p style="margin: 13px 8px">\n                                                    <i class="fa-solid fa-thumbs-up color"></i> <span class="post-like"> 1 </span>\n                                                </p>'):(t+=1,$(e).html(`<p style="margin: 13px 8px">\n                                            <i class="fa-solid fa-thumbs-up color"></i> <span class="post-like"> ${t} </span>\n                                        </p>`))}}$(e).css("color","midnightblue")}else{let l=$(".comment-like");for(let e of l){if($(e).attr("id")==t.likeable){let t=parseInt($(e).text());isNaN(t)?$(e).html('<span>\n                                        <i class="fa-solid fa-thumbs-up color"></i> 1 \n                                    </span>'):(t+=1,$(e).html(`<span>\n                                                <i class="fa-solid fa-thumbs-up color"></i> ${t} \n                                        </span>`))}}$(e).css({color:"midnightblue",fontWeight:"700"})}})).fail((function(t){console.log("error in completing the request",t)}))}))}}let commentbtnloop=function(t){$(t).click((function(t){let e=$(t.currentTarget).attr("id"),l=$(t.currentTarget).attr("data-toggles"),s=$(".post-comments");for(let o of s){$(o).attr("id")==e&&("false"==l?($(o).css("display","block"),$(t.currentTarget).attr("data-toggles",!0)):($(o).css("display","none"),$(t.currentTarget).attr("data-toggles",!1)))}}))},commentbtn=$(".comment-btn");for(let t of commentbtn)commentbtnloop(t);