{let t=$(".add-friend-btn");t.click((function(e){e.preventDefault(),console.log(t.attr("href")),$.ajax({method:"POST",url:t.attr("href"),success:function(e){1==e.request?t.html('<i class="fa-solid fa-user-plus"><button type="submit">Add Friend</button></i>'):t.html('<i class="fa-solid fa-user-xmark"><button type="submit">Remove Friend</button></i>')},error:function(t){console.log(t)}})}))}